    "use strict";

    var AbstractAction = require('web.AbstractAction');
    var core = require('web.core');
    var rpc = require('web.rpc');

    var CompletionDashboard = AbstractAction.extend({
        template: 'CompletionReportDashboard',

        events: {
            'change #doc_type_filter': '_onDocTypeChange',
            'change #report_name_filter': '_onReportNameChange',
            'change #department_filter': '_onDepartmentChange',
        },

        init: function(parent, action) {
            this._super(parent, action);
            this.dashboard_data = {};
            // Store the full, unfiltered lists
            this.all_doc_types = [];
            this.all_report_names = [];
            this.all_departments = [];
            this.myChart = null;
        },

        willStart: function () {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                var def1 = self._fetchFilterData();
                var def2 = self._fetchDashboardData();
                return Promise.all([def1, def2]);
            });
        },

        start: function() {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                setTimeout(function() {
                    self._populateFilters();
                    self._renderDashboard();
                }, 0);
            });
        },

        _fetchFilterData: function() {
            var self = this;
            return rpc.query({
                route: '/report/completion/filters'
            }).then(function(data) {
                self.all_doc_types = data.doc_types;
                self.all_report_names = data.report_names;
                self.all_departments = data.departments;
            });
        },

        _fetchDashboardData: function (filters) {
            var self = this;
            return rpc.query({
                route: '/report/completion/data',
                params: filters || {}
            }).then(function (data) {
                self.dashboard_data = data;
            });
        },

        _populateFilters: function(filtered_reports, filtered_depts) {
            this._populateSelect(this.$('#doc_type_filter'), this.all_doc_types, 'All Document Types');

            var reports_to_show = filtered_reports || this.all_report_names;
            this._populateSelect(this.$('#report_name_filter'), reports_to_show, 'All Report Names');

            var depts_to_show = filtered_depts || this.all_departments;
            this._populateSelect(this.$('#department_filter'), depts_to_show, 'All Departments');
        },

        _populateSelect: function($select, items, default_option_text, current_val) {
            $select.empty();
            $select.append($('<option>', { value: "", text: default_option_text }));
            items.forEach(function(item) {
                $select.append($('<option>', { value: item.id, text: item.name }));
            });
            if (current_val) {
                $select.val(current_val);
            }
        },

        _renderDashboard: function() {
            this.$('.display-4').eq(0).text(this.dashboard_data.total_processed_reports);
            this.$('.display-4').eq(1).text(this.dashboard_data.on_time_reports);
            this.$('.display-4').eq(2).text(this.dashboard_data.late_reports);
            this._renderChart();
        },

        _renderChart: function() {
            var chartDom = this.$('#completion_chart')[0];
            if (chartDom) {
                if (!this.myChart) { this.myChart = echarts.init(chartDom); }
                var option = {
                    tooltip: { trigger: 'item', formatter: '{a} <br/>{b}: {c} ({d}%)' },
                    legend: { orient: 'vertical', left: 10, data: ['On-Time Reports', 'Late Reports'] },
                    series: [{
                        name: 'Report Status', type: 'pie', radius: ['50%', '70%'], avoidLabelOverlap: false,
                        label: { show: false, position: 'center' },
                        emphasis: { label: { show: true, fontSize: '30', fontWeight: 'bold' } },
                        labelLine: { show: false },
                        data: [
                            {value: this.dashboard_data.on_time_reports, name: 'On-Time Reports', itemStyle: { color: '#5cb85c' }},
                            {value: this.dashboard_data.late_reports, name: 'Late Reports', itemStyle: { color: '#d9534f' }}
                        ]
                    }]
                };
                this.myChart.setOption(option, true);
            }
        },

        _updateDashboard: function() {
            var self = this;
            var filters = {
                doc_type_id: this.$('#doc_type_filter').val(),
                conf_id: this.$('#report_name_filter').val(),
                department_id: this.$('#department_filter').val(),
            };
            this._fetchDashboardData(filters).then(function() {
                self._renderDashboard();
            });
        },

        _onDocTypeChange: function() {
            var doc_type_id = parseInt(this.$('#doc_type_filter').val());
            this.$('#report_name_filter').val(''); 
            this.$('#department_filter').val('');

            var filtered_reports = this.all_report_names;
            if (doc_type_id) {
                filtered_reports = this.all_report_names.filter(function(report) {
                    return report.document_type && report.document_type[0] === doc_type_id;
                });
            }
            this._populateSelect(this.$('#report_name_filter'), filtered_reports, 'All Report Names');
            this._populateSelect(this.$('#department_filter'), this.all_departments, 'All Departments');
            this._updateDashboard();
        },

        _onReportNameChange: function() {
            var conf_id = parseInt(this.$('#report_name_filter').val());
            this.$('#department_filter').val('');

            var filtered_depts = this.all_departments;
            if (conf_id) {
                var selected_report = this.all_report_names.find(r => r.id === conf_id);
                if (selected_report) {
                    var dept_ids = selected_report.department_ids;
                    filtered_depts = this.all_departments.filter(d => dept_ids.includes(d.id));
                }
            } else {
                 // If "All Report Names" is selected, we should check the doc_type filter
                var doc_type_id = parseInt(this.$('#doc_type_filter').val());
                if (doc_type_id) {
                    var reports_for_type = this.all_report_names.filter(function(report) {
                        return report.document_type && report.document_type[0] === doc_type_id;
                    });
                    var dept_ids_for_type = new Set();
                    reports_for_type.forEach(r => r.department_ids.forEach(id => dept_ids_for_type.add(id)));
                    filtered_depts = this.all_departments.filter(d => dept_ids_for_type.has(d.id));
                }
            }
            this._populateSelect(this.$('#department_filter'), filtered_depts, 'All Departments');
            this._updateDashboard();
        },

        _onDepartmentChange: function() {
            this._updateDashboard();
        }
    });

    core.action_registry.add('completion_report_dashboard', CompletionDashboard);

    return CompletionDashboard;
});

args.new_string:
odoo.define('odm_report_scheduler.dashboard', function (require) {
    "use strict";

    var AbstractAction = require('web.AbstractAction');
    var core = require('web.core');
    var rpc = require('web.rpc');

    var CompletionDashboard = AbstractAction.extend({
        template: 'CompletionReportDashboard',

        events: {
            'change #doc_type_filter': '_onFilterChange',
            'change #report_name_filter': '_onFilterChange',
            'change #department_filter': '_onFilterChange',
        },

        init: function(parent, action) {
            this._super(parent, action);
            this.dashboard_data = {};
            this.all_doc_types = [];
            this.all_report_names = []; // Will store reports with document_type and department_ids
            this.all_departments = [];
            this.myChart = null;
        },

        willStart: function () {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                // Fetch all filter data once
                var def1 = rpc.query({
                    route: '/report/completion/filters' // This now returns ALL doc types and ALL reports with their relational data
                }).then(function(data) {
                    self.all_doc_types = data.doc_types;
                    self.all_report_names = data.report_names;
                    // Fetch all departments separately as they might not be linked to a report by default for independent filtering
                    return rpc.query({route: '/report/completion/get_departments'}); // This endpoint now just gets ALL departments
                }).then(function(data) {
                    self.all_departments = data;
                });

                var def2 = self._fetchDashboardData(); // Initial fetch with no filters
                return Promise.all([def1, def2]);
            });
        },

        start: function() {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                setTimeout(function() {
                    self._applyFilters(); // Apply filters initially to populate dropdowns correctly
                    self._renderDashboardStats(); // Render only stats initially
                }, 0);
            });
        },

        _fetchDashboardData: function (filters) {
            var self = this;
            return rpc.query({
                route: '/report/completion/data',
                params: filters || {}
            }).then(function (data) {
                self.dashboard_data = data;
            });
        },

        _populateSelect: function($select, items, default_option_text, current_val) {
            $select.empty();
            $select.append($('<option>', { value: "", text: default_option_text }));
            items.forEach(function(item) {
                $select.append($('<option>', { value: item.id, text: item.name }));
            });
            if (current_val && $select.find('option[value="' + current_val + '"]').length) {
                $select.val(current_val);
            } else {
                $select.val(""); // Reset if current value is no longer valid
            }
        },

        _renderDashboardStats: function() {
            this.$('.display-4').eq(0).text(this.dashboard_data.total_processed_reports);
            this.$('.display-4').eq(1).text(this.dashboard_data.on_time_reports);
            this.$('.display-4').eq(2).text(this.dashboard_data.late_reports);
            this._renderChart();
        },

        _renderChart: function() {
            var chartDom = this.$('#completion_chart')[0];
            if (chartDom) {
                if (!this.myChart) { this.myChart = echarts.init(chartDom); }
                var option = {
                    tooltip: { trigger: 'item', formatter: '{a} <br/>{b}: {c} ({d}%)' },
                    legend: { orient: 'vertical', left: 10, data: ['On-Time Reports', 'Late Reports'] },
                    series: [{
                        name: 'Report Status', type: 'pie', radius: ['50%', '70%'], avoidLabelOverlap: false,
                        label: { show: false, position: 'center' },
                        emphasis: { label: { show: true, fontSize: '30', fontWeight: 'bold' } },
                        labelLine: { show: false },
                        data: [
                            {value: this.dashboard_data.on_time_reports, name: 'On-Time Reports', itemStyle: { color: '#5cb85c' }},
                            {value: this.dashboard_data.late_reports, name: 'Late Reports', itemStyle: { color: '#d9534f' }}
                        ]
                    }]
                };
                this.myChart.setOption(option, true);
            }
        },

        _onFilterChange: function() {
            var self = this;
            // Apply filter logic first to update dependent dropdowns
            this._applyFilters();

            var filters = {
                doc_type_id: this.$('#doc_type_filter').val(),
                conf_id: this.$('#report_name_filter').val(),
                department_id: this.$('#department_filter').val(),
            };
            this._fetchDashboardData(filters).then(function() {
                self._renderDashboardStats();
            });
        },

        _applyFilters: function() {
            var $docTypeFilter = this.$('#doc_type_filter');
            var $reportNameFilter = this.$('#report_name_filter');
            var $departmentFilter = this.$('#department_filter');

            var currentDocType = $docTypeFilter.val();
            var currentReportName = $reportNameFilter.val();
            var currentDepartment = $departmentFilter.val();

            // 1. Populate Doc Type Filter (always all doc types)
            this._populateSelect($docTypeFilter, this.all_doc_types, "All Document Types", currentDocType);

            // 2. Filter and Populate Report Name Filter based on selected Doc Type
            var reports_to_show = this.all_report_names;
            if (currentDocType) {
                reports_to_show = this.all_report_names.filter(r => r.document_type && r.document_type[0] == currentDocType);
            }
            this._populateSelect($reportNameFilter, reports_to_show, "All Report Names", currentReportName);

            // 3. Filter and Populate Department Filter based on selected Report Name or Doc Type
            var depts_to_show = this.all_departments;
            if (currentReportName) {
                var selected_report = this.all_report_names.find(r => r.id == currentReportName);
                if (selected_report && selected_report.department_ids && selected_report.department_ids.length > 0) {
                    depts_to_show = this.all_departments.filter(d => selected_report.department_ids.includes(d.id));
                }
                // If report selected but has no specific departments, default to all departments (which is already `depts_to_show`)
            } else if (currentDocType) {
                // If no specific report, but a doc type is selected, show departments from all relevant reports
                let relevant_reports = this.all_report_names.filter(r => r.document_type && r.document_type[0] == currentDocType);
                let unique_dept_ids = new Set();
                relevant_reports.forEach(r => {
                    if (r.department_ids) {
                        r.department_ids.forEach(id => unique_dept_ids.add(id));
                    }
                });
                if (unique_dept_ids.size > 0) {
                    depts_to_show = this.all_departments.filter(d => unique_dept_ids.has(d.id));
                } else {
                     // If reports for this doc type have no departments, default to all
                     depts_to_show = this.all_departments;
                }
            } else {
                // If neither Doc Type nor Report Name is selected, show all departments (which is already `depts_to_show`)
                depts_to_show = this.all_departments;
            }
            this._populateSelect($departmentFilter, depts_to_show, "All Departments", currentDepartment);
        },
    });

    core.action_registry.add('completion_report_dashboard', CompletionDashboard);

    return CompletionDashboard;
});

