odoo.define('odm_report_scheduler.dashboard', function (require) {
    "use strict";

    var AbstractAction = require('web.AbstractAction');
    var core = require('web.core');
    var rpc = require('web.rpc');

    var CompletionDashboard = AbstractAction.extend({
        template: 'CompletionReportDashboard',

        events: {
            'change #doc_type_filter': '_onFilterChange',
            'change #report_name_filter': '_onFilterChange',
            'change #department_filter': '_onFilterChange',
        },

        init: function(parent, action) {
            this._super(parent, action);
            this.dashboard_data = {};
            this.all_doc_types = [];
            this.all_report_configs = []; // Renamed from all_report_names to reflect it holds full configs
            this.all_departments = [];
            this.myChart = null;
        },

        willStart: function () {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                var initialFiltersPromise = rpc.query({
                    route: '/report/completion/filters'
                }).then(function(data) {
                    self.all_doc_types = data.doc_types;
                    self.all_report_configs = data.report_configs; // Now receiving full configs
                    return rpc.query({route: '/report/completion/get_departments'}); // Get ALL departments
                }).then(function(data) {
                    self.all_departments = data;
                });

                var dashboardDataPromise = self._fetchDashboardData();
                return Promise.all([initialFiltersPromise, dashboardDataPromise]);
            });
        },

        start: function() {
            var self = this;
            return this._super.apply(this, arguments).then(function () {
                setTimeout(function() {
                    self._applyFiltersAndPopulateDropdowns(); // Populate all dropdowns initially
                    self._renderDashboardStats();
                }, 0);
            });
        },

        _fetchDashboardData: function (filters) {
            var self = this;
            return rpc.query({
                route: '/report/completion/data',
                params: filters || {}
            }).then(function (data) {
                self.dashboard_data = data;
            });
        },

        _populateSelect: function($select, items, default_option_text, current_val) {
            $select.empty();
            $select.append($('<option>', { value: "", text: default_option_text }));
            items.forEach(function(item) {
                $select.append($('<option>', { value: item.id, text: item.name }));
            });
            // Try to keep current value if still valid, otherwise reset
            if (current_val && $select.find('option[value="' + current_val + '"]').length) {
                $select.val(current_val);
            } else {
                $select.val("");
            }
        },

        _renderDashboardStats: function() {
            this.$('.display-4').eq(0).text(this.dashboard_data.total_processed_reports);
            this.$('.display-4').eq(1).text(this.dashboard_data.on_time_reports);
            this.$('.display-4').eq(2).text(this.dashboard_data.late_reports);
            this._renderChart();
        },

        _renderChart: function() {
            var chartDom = this.$('#completion_chart')[0];
            if (chartDom) {
                if (!this.myChart) { this.myChart = echarts.init(chartDom); }
                var option = {
                    tooltip: { trigger: 'item', formatter: '{a} <br/>{b}: {c} ({d}%)' },
                    legend: { orient: 'vertical', left: 10, data: ['On-Time Reports', 'Late Reports'] },
                    series: [{
                        name: 'Report Status', type: 'pie', radius: ['50%', '70%'], avoidLabelOverlap: false,
                        label: { show: false, position: 'center' },
                        emphasis: { label: { show: true, fontSize: '30', fontWeight: 'bold' } },
                        labelLine: { show: false },
                        data: [
                            {value: this.dashboard_data.on_time_reports, name: 'On-Time Reports', itemStyle: { color: '#5cb85c' }},
                            {value: this.dashboard_data.late_reports, name: 'Late Reports', itemStyle: { color: '#d9534f' }}
                        ]
                    }]
                };
                this.myChart.setOption(option, true);
            }
        },

        _onFilterChange: function() {
            var self = this;
            this._applyFiltersAndPopulateDropdowns(); // First, update all dropdowns based on new selection

            var filters = {
                doc_type_id: this.$('#doc_type_filter').val() || null,
                conf_id: this.$('#report_name_filter').val() || null,
                department_id: this.$('#department_filter').val() || null,
            };
            this._fetchDashboardData(filters).then(function() {
                self._renderDashboardStats();
            });
        },

        _applyFiltersAndPopulateDropdowns: function() {
            var $docTypeFilter = this.$('#doc_type_filter');
            var $reportNameFilter = this.$('#report_name_filter');
            var $departmentFilter = this.$('#department_filter');

            var currentDocTypeVal = $docTypeFilter.val();
            var currentReportNameVal = $reportNameFilter.val();
            var currentDepartmentVal = $departmentFilter.val();

            // 1. Always populate Doc Type Filter with all available types
            this._populateSelect($docTypeFilter, this.all_doc_types, "All Document Types", currentDocTypeVal);

            // 2. Filter and Populate Report Name Filter based on selected Doc Type
            var reports_to_show = this.all_report_configs;
            if (currentDocTypeVal) {
                reports_to_show = this.all_report_configs.filter(r => r.document_type && r.document_type[0] == parseInt(currentDocTypeVal));
            }
            this._populateSelect($reportNameFilter, reports_to_show, "All Report Names", currentReportNameVal);

            // 3. Filter and Populate Department Filter based on selected Report Name (if any) or Doc Type
            var departments_to_show = this.all_departments;
            if (currentReportNameVal) {
                var selected_report_config = this.all_report_configs.find(r => r.id == parseInt(currentReportNameVal));
                if (selected_report_config && selected_report_config.department_ids && selected_report_config.department_ids.length > 0) {
                    var specific_dept_ids = selected_report_config.department_ids.map(d => d); // department_ids is already a list of IDs from search_read
                    departments_to_show = this.all_departments.filter(d => specific_dept_ids.includes(d.id));
                }
                // If a report is selected but has no specific departments, departments_to_show remains all_departments.
            } else if (currentDocTypeVal) {
                // If no specific report, but a doc type is selected, collect departments from all relevant reports
                let reports_for_doc_type = this.all_report_configs.filter(r => r.document_type && r.document_type[0] == parseInt(currentDocTypeVal));
                let unique_dept_ids = new Set();
                reports_for_doc_type.forEach(r => {
                    if (r.department_ids) {
                        r.department_ids.forEach(id => unique_dept_ids.add(id));
                    }
                });
                if (unique_dept_ids.size > 0) {
                    departments_to_show = this.all_departments.filter(d => unique_dept_ids.has(d.id));
                }
                // If reports for this doc type have no departments, departments_to_show remains all_departments.
            }
            // If neither Doc Type nor Report Name is selected, departments_to_show remains all_departments.
            this._populateSelect($departmentFilter, departments_to_show, "All Departments", currentDepartmentVal);
        },
    });

    core.action_registry.add('completion_report_dashboard', CompletionDashboard);

    return CompletionDashboard;
});

