def _generate_future_submissions(self):
    for config in self:
        # Hapus schedule yang belum submitted
        future_submissions = self.env['odm.report.submission'].search([
            ('conf_id', '=', config.id),
            ('deadline_time', '>', datetime.now()),
            ('state', '!=', 'completed')
        ])
        future_submissions.unlink()

        # Tentukan departemen
        departments = config.department_ids or self.env['hr.department'].search([])

        # Buat jadwal baru
        for dept in departments:
            now = datetime.now()
            for i in range(24):  # Generate untuk 2 tahun ke depan
                if config.reporting_period == 'monthly':
                    next_date = now + timedelta(days=30 * i)
                    day = min(config.report_date, monthrange(next_date.year, next_date.month)[1])
                    deadline = datetime(next_date.year, next_date.month, day)
                elif config.reporting_period in ['quarterly', 'semester']:
                    # Tentukan bulan berdasarkan periode
                    months = []
                    if config.reporting_period == 'quarterly':
                        months = [3, 6, 9, 12]
                    elif config.reporting_period == 'semester':
                        months = [6, 12]

                    # Cari bulan berikutnya yang sesuai
                    next_month = None
                    for month in months:
                        if month > now.month:
                            next_month = month
                            break
                    if not next_month:
                        now = now.replace(year=now.year + 1)
                        next_month = months[0]

                    day = min(config.report_date, monthrange(now.year, next_month)[1])
                    deadline = datetime(now.year, next_month, day)
                else:
                    continue

                if deadline > datetime.now():
                    self.env['odm.report.submission'].create({
                        'conf_id': config.id,
                        'name': config.name,
                        'submission_freq': config.reporting_period,
                        'doc_type': config.document_type.id,
                        'deadline_time': deadline,
                        'department_id': dept.id,
                    })

@api.model
def create(self, vals):
    res = super(DocumentConfiguration, self).create(vals)
    res._generate_future_submissions()
    return res
